import React, { useState } from "react";
import FormAlert from "./FormAlert";
import Section from "./Section";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Avatar3 from "./Avatar3";
import { useRouter } from "./../util/router.js";
import { useAuth } from "./../util/auth.js";

function CreateProfileSection(props) {
    const auth = useAuth();
    const router = useRouter();
    const [formAlert, setFormAlert] = useState(null);

    let userPassword = "";

    const handleAuth = (user) => {
        router.push(props.afterAuthPath);
    };

    const onFormAlert = (data) => {
      setFormAlert(data);
    };

    const submitHandlersByType = {
      signup: ({ email, pass }) => {
        return auth.signup(email, pass).then((user) => {
          console.log('checked');
          // Call auth complete handler
          // handleAuth(user);
        });
      },
    };

    const handleSubmit = (event) => {
      event.preventDefault();
      let userEmail = props.email;
      submitHandlersByType['signup']({
        userEmail,
        userPassword,
      }).catch((error) => {
        // Show error alert message
        onFormAlert({
          type: "error",
          message: error.message,
        });
      });
    };

    const changePasswordHandler = (event) => {
      userPassword = event.target.value;
    }

    return (
        <Section
            bg={props.bg}
            textColor={props.textColor}
            size={props.size}
            bgImage={props.bgImage}
            bgImageOpacity={props.bgImageOpacity}
        >
            {formAlert && (
              <FormAlert
                type={formAlert.type}
                message={formAlert.message}
            ></FormAlert>
            )}
            <Col xs={12} className="p-4 text-center">
                <Avatar3
                  src="https://i.ibb.co/tpMMRH8/Group-34.png"
                  alt=""
                  size="90px"
                  className="text-center"
                ></Avatar3>
              </Col>
              <Form onSubmit={handleSubmit}>
              <Col xs={10}>
                  <Form.Group controlId="user_image">
                    <Form.Label>Image Upload</Form.Label>
                    <Form.Control
                      type="file"
                      placeholder="Enter email"
                    ></Form.Control>
                  </Form.Group>
                </Col>
                <Col xs={10}>
                  <Form.Group controlId="user_name">
                    <Form.Label>Full Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Otis Ottille..."
                    ></Form.Control>
                  </Form.Group>
                </Col>
                <Form.Row className="ml-3">
                  <Form.Group controlId="user_school">
                    <Form.Label>School Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Otter Space University"
                    ></Form.Control>
                  </Form.Group>
                  <Form.Group controlId="user_year" className="ml-3">
                    <Form.Label>Year</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Class of 2020..."
                    ></Form.Control>
                  </Form.Group>
                </Form.Row>
                <Col xs={10}>
                  <Form.Group controlId="user_website">
                    <Form.Label>Create a Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Type a password..."
                      onChange={changePasswordHandler}
                    ></Form.Control>
                  </Form.Group>
                </Col>
                <Col xs={10}>
                  <Form.Group controlId="user_website">
                    <Form.Label>LinkedIn (optional)</Form.Label>
                    <Form.Control
                      type="url"
                      placeholder="https://otterspace.com"
                    ></Form.Control>
                  </Form.Group>
                </Col>
                <Col xs={10}>
                  <Form.Group controlId="user_website">
                    <Form.Label>Website (optional)</Form.Label>
                    <Form.Control
                      type="url"
                      placeholder="https://otterspace.com"
                    ></Form.Control>
                  </Form.Group>
                </Col>
                <Col className="text-center mt-2 mb-4">
                  <Button variant="purple" size="md" type="submit">
                    <span className="HeroSection__form-submit">
                      Get Started
                    </span>
                  </Button>
                </Col>
              </Form>
        </Section>
    );
}

export default CreateProfileSection;
