// Firebase variables
var db = firebase.firestore();
var postsRef = db.collection("posts");

// Post Data
var userName = "";
var userImage = "";
var userClass = "";
var userSchool = "";
var postTitle = "";
var postDescription = "";
var postIndustry = "";

class DashboardSection extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isHidden: false,
            createPost: false,
            posts: [],
            user: firebase.auth().currentUser,
        };
    };

    componentDidMount() {
        // firebase methods
        postsRef.get().then((querySnapshot) => {
            let posts = [];
            querySnapshot.forEach((doc) => {
              posts.push(doc.data());
            });
            this.setState({posts: posts});
        });
        var userRef = db.collection("users").doc(this.state.user.uid);
        userRef.get().then(function(doc) {
            if (doc.exists) {
                userClass = doc.data().year;
                userSchool = doc.data().school;
                userName = doc.data().name;
                userImage = doc.data().image;

            } else {
                console.log("No such document!");
            }
          }).catch(function(error) {
            console.log("Error getting document:", error);
          });
    }

    showCreate = () => {
        this.setState({
            createPost: true,
        });
    };

    hideCreate = () => {
        this.setState({
            createPost: false,
        });
    };

    hideCard = () => {
        this.setState({
            isHidden: true,
        });
    };


    handleSubmit = (event) => {
        event.preventDefault();
        console.log(postDescription);
        console.log(userClass);
        console.log(userImage);
        console.log(userName);
        console.log(postIndustry);
        console.log(postTitle);
        console.log(userSchool);
        if (postTitle === "" || postDescription === "" || postIndustry === "") {
            event.preventDefault();
            alert('Missing fields!');
        }
        else {
            db.collection("posts").add({
                body: postDescription,
                class: userClass,
                image: userImage,
                name: userName,
                tag: postIndustry,
                title: postTitle,
                university: userSchool,
            })
                .then(function (docRef) {
                    console.log("Document written with ID: ", docRef.id);
                })
                .catch(function (error) {
                    console.error("Error adding document: ", error);
                });
        }
    };

    changeTitleHandler = (event) => {
        postTitle = event.target.value;
    }

    changeDescriptionHandler = (event) => {
        postDescription = event.target.value;
    }

    changeIndustryHandler = (event) => {
        postIndustry = event.target.value;
    }

    render() {
        return (
            <Section
                bg={this.props.bg}
                textColor={this.props.textColor}
                size={this.props.size}
                bgImage={this.props.bgImage}
                bgImageOpacity={this.props.bgImageOpacity}
            >
                <Container fluid={true} sticky="top">
                    <Row>
                        <Col xs={3} md={2} className="d-none d-sm-block">
                            <Container fluid={true}>
                                <Row>
                                    <Col xs={12} className="text-center">
                                        <Avatar2
                                            src={userImage}
                                            size="56px"
                                        ></Avatar2>
                                    </Col>
                                    <Col xs={12} className="text-center mt-1">
                                        <span className="DashboardSection__profile-name" href="/">
                                            {userName}
                                        </span>
                                    </Col>
                                    <Col xs={12} className="text-center">
                                        <Link
                                            className="DashboardSection__profile-link"
                                            to="/settings/general"
                                        >
                                            Edit Profile
                  </Link>
                                    </Col>
                                    <Col xs={12} className="mt-3">
                                        <Link
                                            className="DashboardSection__sidebar-link"
                                            to="/dashboard"
                                        >
                                            Home
                  </Link>
                                    </Col>
                                    <Col xs={12} className="mt-3">
                                        <div className="DashboardSection__border"></div>
                                    </Col>
                                    <Col xs={12} className="mt-3">
                                        <span className="DashboardSection__workspace-title" href="/">
                                            WorkSpaces
                  </span>
                                    </Col>
                                    <Col xs={12} className="mt-3">
                                        <Link
                                            className="DashboardSection__project-link"
                                            to="/workspace/kanban"
                                        >
                                            Design Project 1
                  </Link>
                                    </Col>
                                    <Col xs={12} className="mt-3">
                                        <Link
                                            className="DashboardSection__project-link"
                                            to="/workspace/kanban"
                                        >
                                            Design Project 2
                  </Link>
                                    </Col>
                                </Row>
                            </Container>
                        </Col>
                        <Col xs={6} md={7}>
                            <Card className="w-100 bg-purple mb-3" hidden={this.state.isHidden}>
                                <Card.Body className="text-center">
                                    <Card.Title>
                                        <h4 className="DashboardSection__create-workspace-title">
                                            Work with other students on projects
                  </h4>
                                    </Card.Title>
                                    <Card.Text>
                                        <p className="DashboardSection__create-workspace-text">
                                            Find projects to work on with other students - filtered by
                                            industry and interests
                  </p>
                                    </Card.Text>
                                    <Button variant="light" size="md" className="px-4">
                                        <span className="DashboardSection__done-button">Next</span>
                                    </Button>
                                    <Col xs={12} className="mt-2">
                                        <LinkContainer to="#">
                                            <Card.Link onClick={this.hideCard}>
                                                <span className="DashboardSection__skip-for-now">
                                                    Skip for now
                      </span>
                                            </Card.Link>
                                        </LinkContainer>
                                    </Col>
                                </Card.Body>
                            </Card>
                            <SectionHeader2
                                title={this.props.title}
                                subtitle={this.props.subtitle}
                                size={1}
                                spaced={true}
                                className="text-left"
                                onClick={this.showCreate}
                            ></SectionHeader2>
                            <Modal show={this.state.createPost} onHide={this.hideCreate}>
                                <Form onSubmit={this.handleSubmit}>
                                    <Form.Row>
                                        <Col xs={10} className="ml-4 mt-3">
                                            <Form.Group controlId="post_title">
                                                <Form.Control
                                                    type="text"
                                                    placeholder="I am looking for..."
                                                    onChange={this.changeTitleHandler}
                                                ></Form.Control>
                                            </Form.Group>
                                        </Col>
                                        <Col xs={10} className="ml-4">
                                            <Form.Group controlId="post_description">
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Type a description..."
                                                    as="textarea"
                                                    rows={4}
                                                    onChange={this.changeDescriptionHandler}
                                                ></Form.Control>
                                            </Form.Group>
                                        </Col>
                                        <Col xs={10} className="ml-4">
                                            <Form.Group controlId="post_industry">
                                                <Form.Control
                                                    as="select"
                                                    defaultValue="Select an industry"
                                                    onChange={this.changeIndustryHandler}

                                                >
                                                    <option>Select an industry</option>
                                                    <option>Web Dev</option>
                                                    <option>Design</option>
                                                    <option>Product Management</option>
                                                </Form.Control>
                                            </Form.Group>
                                        </Col>
                                    </Form.Row>
                                    <Form.Row className="mb-3">
                                        <Col xs={12} className="text-right">
                                            <Button
                                                variant="purple"
                                                size="md"
                                                className="px-4 mr-4"
                                                type="submit"
                                            >
                                                <span className="DashboardSection__post">Post</span>
                                            </Button>
                                        </Col>
                                    </Form.Row>
                                </Form>
                            </Modal>
                            <HomeSelector
                                items={this.state.posts}
                            ></HomeSelector>
                            <div
                                className="mt-5 mx-auto text-center"
                                style={{
                                    maxWidth: "460px",
                                }}
                            >
                            </div>
                        </Col>
                        <Col className="d-none d-sm-block"></Col>
                    </Row>
                </Container>
            </Section>
        );
    }
}

export default DashboardSection;
