import React, { useState } from "react";
import Section from "./Section";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Avatar2 from "./Avatar2";
import { Link, useRouter } from "./../util/router.js";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import { LinkContainer } from "react-router-bootstrap";
import SectionHeader2 from "./SectionHeader2";
import HomeSelector from "./HomeSelector";
import ListGroup from "react-bootstrap/ListGroup";
import { useAuth } from "./../util/auth.js";
import "./DashboardSection.scss";
import firebase from "./../util/firebase";

// Firebase variables
var db = firebase.firestore();
var postsRef = db.collection("posts");
var userRef = db.collection("users").doc("39fhIixPACJ99JEHvcPU");

// Post Data
let posts = [];
let userName = "";
let userImage = "";
let userClass = "";
let userSchool = "";

// Firebase methods
userRef.get().then(function(doc) {
  if (doc.exists) {
      userClass = doc.data().class;
      userImage = doc.data().image;
      userName = doc.data().name;
      userSchool = doc.data().school;
  } else {
      console.log("No such document!");
  }
}).catch(function(error) {
  console.log("Error getting document:", error);
});

postsRef.get().then((querySnapshot) => {
  querySnapshot.forEach((doc) => {
    posts.push(doc.data());
  });
});

function DashboardSection(props) {
  const auth = useAuth();
  // const router = useRouter();

  const [isHidden, cardIsHidden] = React.useState(false);
  const [showCreatePost, createPost] = React.useState(false);

  // Firebase variables
  var user = firebase.auth().currentUser;
  var userRef = db.collection("users").doc(user.uid);
  var postsRef = db.collection("posts");

  // Post Data
    let posts = [];
    let userName = "";
let userImage = "";
  let userClass = "";
  let userSchool = "";

  var postTitle = "";
  var postDescription = "";
  var postIndustry = "";

  const showCreate = () => {
    createPost(true);
  };

  const hideCreate = () => {
    createPost(false);
  };

  const hideCard = () => {
    cardIsHidden(true);
  };

  const handleSubmit = async (event) => {
    if(postTitle === "" || postDescription === "" || postIndustry === "") {
      event.preventDefault();
    }
    else {
      await db.collection("posts").add({
        body: postDescription,
        class: userClass,
        image: userImage,
        name: userName,
        tag: postIndustry,
        title: postTitle,
        university: userSchool,
      })
      .then(function(docRef) {
        console.log("Document written with ID: ", docRef.id);
      })
      .catch(function(error) {
        console.error("Error adding document: ", error);
      });
    }
  };

  const getUsers = async (event) => {
    await userRef.get().then(function(doc) {
        if (doc.exists) {
            userClass = doc.data().class;
            userImage = doc.data().image;
            userName = doc.data().name;
            userSchool = doc.data().school;
        } else {
            console.log("No such document!");
        }
      }).catch(function(error) {
        console.log("Error getting document:", error);
      });
  };

  const changeTitleHandler = (event) => {
    postTitle = event.target.value;
  }

  const changeDescriptionHandler = (event) => {
    postDescription = event.target.value;
  }

  const changeIndustryHandler = (event) => {
    postIndustry = event.target.value;
  }

  return (
    <Section
      bg={props.bg}
      textColor={props.textColor}
      size={props.size}
      bgImage={props.bgImage}
      bgImageOpacity={props.bgImageOpacity}
    >
    {/* {getUsers} */}
    {/* {getPosts} */}
      <Container fluid={true} sticky="top">
        <Row>
          <Col xs={3} md={2} className="d-none d-sm-block">
            <Container fluid={true}>
              <Row>
                <Col xs={12} className="text-center">
                  <Avatar2
                    src={userImage}
                    size="56px"
                  ></Avatar2>
                </Col>
                <Col xs={12} className="text-center mt-1">
                  <span className="DashboardSection__profile-name" href="/">
                    {userName}
                  </span>
                </Col>
                <Col xs={12} className="text-center">
                  <Link
                    className="DashboardSection__profile-link"
                    to="/settings/general"
                  >
                    Edit Profile
                  </Link>
                </Col>
                <Col xs={12} className="mt-3">
                  <Link
                    className="DashboardSection__sidebar-link"
                    to="/dashboard"
                  >
                    Home
                  </Link>
                </Col>
                <Col xs={12} className="mt-3">
                  <div className="DashboardSection__border"></div>
                </Col>
                <Col xs={12} className="mt-3">
                  <span className="DashboardSection__workspace-title" href="/">
                    WorkSpaces
                  </span>
                </Col>
                <Col xs={12} className="mt-3">
                  <Link
                    className="DashboardSection__project-link"
                    to="/workspace/kanban"
                  >
                    Design Project 1
                  </Link>
                </Col>
                <Col xs={12} className="mt-3">
                  <Link
                    className="DashboardSection__project-link"
                    to="/workspace/kanban"
                  >
                    Design Project 2
                  </Link>
                </Col>
              </Row>
            </Container>
          </Col>
          <Col xs={6} md={7}>
            <Card className="w-100 bg-purple mb-3" hidden={isHidden}>
              <Card.Body className="text-center">
                <Card.Title>
                  <h4 className="DashboardSection__create-workspace-title">
                    Work with other students on projects
                  </h4>
                </Card.Title>
                <Card.Text>
                  <p className="DashboardSection__create-workspace-text">
                    Find projects to work on with other students - filtered by
                    industry and interests
                  </p>
                </Card.Text>
                <Button variant="light" size="md" className="px-4">
                  <span className="DashboardSection__done-button">Next</span>
                </Button>
                <Col xs={12} className="mt-2">
                  <LinkContainer to="#">
                    <Card.Link onClick={hideCard}>
                      <span className="DashboardSection__skip-for-now">
                        Skip for now
                      </span>
                    </Card.Link>
                  </LinkContainer>
                </Col>
              </Card.Body>
            </Card>
            <SectionHeader2
              title={props.title}
              subtitle={props.subtitle}
              size={1}
              spaced={true}
              className="text-left"
              onClick={showCreate}
            ></SectionHeader2>
            <Modal show={showCreatePost} onHide={hideCreate}>
              <Form onSubmit={handleSubmit}>
                <Form.Row>
                  <Col xs={10} className="ml-4 mt-3">
                    <Form.Group controlId="post_title">
                      <Form.Control
                        type="text"
                        placeholder="I am looking for..."
                        onChange={changeTitleHandler}
                      ></Form.Control>
                    </Form.Group>
                  </Col>
                  <Col xs={10} className="ml-4">
                    <Form.Group controlId="post_description">
                      <Form.Control
                        type="text"
                        placeholder="Type a description..."
                        as="textarea"
                        rows={4}
                        onChange={changeDescriptionHandler}
                      ></Form.Control>
                    </Form.Group>
                  </Col>
                  <Col xs={10} className="ml-4">
                    <Form.Group controlId="post_industry">
                      <Form.Control
                        as="select"
                        defaultValue="Select an industry"
                        onChange={changeIndustryHandler}

                      >
                          <option>Select an industry</option>
                          <option>Web Dev</option>
                          <option>Design</option>
                          <option>Product Management</option>
                      </Form.Control>
                    </Form.Group>
                  </Col>
                </Form.Row>
                <Form.Row className="mb-3">
                  <Col xs={12} className="text-right">
                    <Button
                      variant="purple"
                      size="md"
                      className="px-4 mr-4"
                      type="submit"
                    >
                      <span className="DashboardSection__post">Post</span>
                    </Button>
                  </Col>
                </Form.Row>
              </Form>
            </Modal>
            <HomeSelector
              items={posts}
            ></HomeSelector>
            <div
              className="mt-5 mx-auto text-center"
              style={{
                maxWidth: "460px",
              }}
            >
              <small>Some helpful debug info üêõ</small>
              <ListGroup className="mt-2">
                <ListGroup.Item>
                  Logged in as <strong>{auth.user.email}</strong>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Link to="/settings/general">Account settings</Link>
                </ListGroup.Item>
              </ListGroup>
            </div>
          </Col>
          <Col className="d-none d-sm-block"></Col>
        </Row>
      </Container>
    </Section>
  );
}

