import React, { useState } from "react";
import Section from "./Section";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import SectionHeader from "./SectionHeader";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Image from "react-bootstrap/Image";
import "./HeroSection.scss";
import CreateProfileSection from "./CreateProfileSection";
import { useRouter } from "./../util/router.js";

function HeroSection(props) {
  const router = useRouter();
  let userEmail = "";

  const [isOpen, setIsOpen] = React.useState(false);

  const showModal = () => {
      setIsOpen(true);
  };

  const hideModal = () => {
    setIsOpen(false);
  };

  const handleSubmit = (event) => {
    if(userEmail !== "") {
      showModal();
    }
    event.preventDefault();

  };

  const changeEmailHandler = (event) => {
    userEmail = event.target.value;
    console.log(userEmail);
  }

  return (
    <Section
      bg={props.bg}
      textColor={props.textColor}
      size={props.size}
      bgImage={props.bgImage}
      bgImageOpacity={props.bgImageOpacity}
    >
      <Container>
        <Row className="align-items-center">
          <Col lg={7} className="text-center text-lg-left">
            <SectionHeader
              title={props.title}
              subtitle={props.subtitle}
              size={1}
              spaced={true}
            ></SectionHeader>
            <Modal show={isOpen} onHide={hideModal}>
              <Modal.Title className="text-center mt-4 mb-2">
                <span className="HeroSection__create-profile">
                  Create your profile
                </span>
              </Modal.Title>
              <CreateProfileSection 
                email = {userEmail}
                afterAuthPath={router.query.next || "/dashboard"}
              >

              </CreateProfileSection>
            </Modal>
            <Form onSubmit={handleSubmit}>
              <Row>
                <Col xs="auto">
                  <Form.Group controlId="email">
                    <Form.Control
                      type="email"
                      placeholder="Enter email here"
                      onChange={changeEmailHandler}
                    ></Form.Control>
                  </Form.Group>
                </Col>
                <Col xs="auto">
                  <Button
                    variant="purple"
                    size="md px-5 ml-2"
                    type="submit"
                  >
                    <span className="HeroSection__signup">Sign Up</span>
                  </Button>
                </Col>
              </Row>
            </Form>
          </Col>
          <Col className="offset-lg-1 mt-5 mt-lg-0">
            <figure className="HeroSection__image-container mx-auto">
              <Image src={props.image} fluid={true}></Image>
            </figure>
          </Col>
        </Row>
      </Container>
    </Section>
  );
}

export default HeroSection;
