import React from "react";
import Section from "./Section";
import Container from "react-bootstrap/Container";
import SectionHeader from "./SectionHeader";
import SchoolSelection from "./SchoolSelection";

function SchoolsSection(props) {
  return (
    <Section
      bg={props.bg}
      textColor={props.textColor}
      size={props.size}
      bgImage={props.bgImage}
      bgImageOpacity={props.bgImageOpacity}
    >
      <Container>
        <SectionHeader
          title={props.title}
          subtitle={props.subtitle}
          size={2}
          spaced={true}
          className="text-center"
        ></SectionHeader>
        <SchoolSelection
          items={[
            {
              avatar:
                "https://ca-times.brightspotcdn.com/dims4/default/ff3c19a/2147483647/strip/true/crop/1000x650+0+0/resize/840x546!/quality/90/?url=https%3A%2F%2Fcalifornia-times-brightspot.s3.amazonaws.com%2Fc4%2Fb4%2F0a11793b7f6ead9e7463596fde80%2Fla-sp-sn-ucla-basketball-oklahoma-john-wooden-001",
              name: "UCLA",
            },
            {
              avatar:
                "data:image/jpeg;base64,",
              name: "UC Berkeley",
              href: "berkeley",
            },
            {
              avatar:
                "https://www.clipartkey.com/mpngs/m/169-1690929_usc-trojans-logo-png.png",
              name: "USC",
            },
            {
              avatar:
                "data:image/png;base64,",
              name: "Stanford",
            },
            {
              avatar:
                "data:image/png;base64,",
              name: "Harvard",
            },
            {
              avatar:
                "data:image/png;base64,",
              name: "Princeton",
            },
            {
              avatar:
                "https://pbs.twimg.com/profile_images/1037111953573982209/hHrPXjyu.jpg",
              name: "Brown",
            },
            {
              avatar:
                "https://ih1.redbubble.net/image.855676207.6552/flat,750x,075,f-pad,750x1000,f8f8f8.u2.jpg",
              name: "Yale",
            },
          ]}
        ></SchoolSelection>
      </Container>
    </Section>
  );
}

export default SchoolsSection;
